import React, { useState, useRef } from 'react'
import { useGLTF } from '@react-three/drei'

export default function Model(props) {
  const group = useRef()
  const { nodes, materials } = useGLTF('/brasero.gltf')
  const [colour, setColour] = useState("#ED91AD")

  return (
    <group ref={group} {...props} dispose={null}>
      <mesh geometry={nodes.Hibachi.geometry} material={nodes.Hibachi.material}>
        <mesh onClick={() => { colour === "#ED91AD" ? setColour("#93c572") : setColour("#ED91AD") }} geometry={nodes.Hibachi_Vase_0.geometry} material={nodes.Hibachi_Vase_0.material} material-color={colour} />
      </mesh>
      <group rotation={[-Math.PI / 2, 0, 0]}>
        <group rotation={[Math.PI / 2, 0, 0]}>
          <group scale={100}>
            <mesh geometry={nodes.Hibachi_Vase_0.geometry} material={materials.Vase} />
            <mesh geometry={nodes.Hibachi_Lid_0.geometry} material={materials.material} />
          </group>
        </group>
      </group>
    </group>
  )
}

useGLTF.preload('/brasero.gltf')
