import axios from 'axios';
import { useEffect, useState } from 'react';
import './Courses.css';
import { BiShekel } from "react-icons/bi"



export function Courses() {
    const [courses, setCourses] = useState([{
        courseId: "",
        courseName: "",
        coursePic: "",
        startDate: "",
        price: ""
    }]);

    const url = 'https://127.0.0.1:5789/courses'



    // use get request to post the courses from the DB(get request)
    useEffect(() => {
        axios.get(url)
            .then(courses => {
                setCourses(courses.data);
            });

    }, []);

    return (
        <div className='courses'>
            {courses && <div>
                {courses.map((curr, i) => {
                    console.log(curr);
                    return <Coursecard key={i}{...curr}></Coursecard>;


                })}

            </div>}
        </div>
    );
}



const Coursecard = (props: {
    courseId: string,
    courseName: string,
    coursePic: string,
    startDate: string,
    price: string,

}) => {
    const url = 'https://127.0.0.1:5789/courses';

    return (
        <div className='coursecard'>
            <ul>
                <li className='courseName'> Course Name {props.courseName} </li>
                <li> <img src={props.coursePic} alt="" /></li>
                <li className='price'> <BiShekel></BiShekel>{props.price}</li>
                <li className='startDate'> Start Date {props.startDate}</li>

            </ul>
        </div>
    )
}
